#!/usr/bin/env bash


PROGRAM_TYPE=$1
USERNAME=$2

[ -z $USERNAME ] && echo "[-] enter username" && exit
mkdir -p $USERNAME

if [ $PROGRAM_TYPE == "go" ]; then
    SCRIPT_DIR=$( cd -- "$( dirname -- "${BASH_SOURCE[0]}" )" &> /dev/null && pwd )
    GO_PROGRAM="$SCRIPT_DIR/../go/app"

    run () {
        echo -e "TEST $1\n"
        $GO_PROGRAM -t $1 -o test.txt
        [ $? -eq 0 ] && (echo "Test $1 good"; mv test.txt "$USERNAME/test_$1.txt") || (echo "Test $1 partial"; mv test.txt "$USERNAME/test_$1.partial.txt")
    }

    run 1
    run 2
    run 3
    run 4
    run 5
    run 6
    run 7
    run 8
    run 9
    run 10
    run 11
    run 12
    run 13
    run 14
    run 15
    run 16
    run 17
    run 18
    run 19
fi
else
    SCRIPT_DIR=$( cd -- "$( dirname -- "${BASH_SOURCE[0]}" )" &> /dev/null && pwd )
    PYTHON_PROGRAM="$SCRIPT_DIR/../poc/main.py"

    run () {
        echo -e "TEST $1\n"
        pypy3 $PYTHON_PROGRAM $2 $3 $4 $5
        [ $? -eq 0 ] && (echo "Test $1 good"; mv test.txt "$USERNAME/test_$1.txt") || (echo "Test $1 partial"; mv test.txt "$USERNAME/test_$1.partial.txt")
